/*
author: MIKE MAMBWE
Program: a react todo list APP
Contact: +260975854067
Email : engineermambwe@gmail.com
*/
//this program uses react hooks to store and output the information in it.
import React,{useState} from 'react';
import './App.css';
//import statement are placed above this line 


function App() {
  //useState - hook
  const [item, setItem] = useState("");
  //The array belew holds the values of the entered items on the list.
  const [newItem, setNewItem] = useState([]);

  //The helper function to help add values to the list.
  const addItem =()=>{
    const listItem = {
      id: Math.floor(Math.random() * 1000),
      value:newItem,

    };
    setNewItem(oldList => [...oldList,newItem])
    setItem("")//this is reset to the default empty string.

    console.log(newItem)
  }

  return (
    <div className="App">
      {/* The header of this project is below this line. */}
     <h1>TodoList React APP!</h1>
      {/* The Input field and the button */}
    <input type="text"
      placeholder='Please enter...'
      value={item}
      onChange={e => setItem(e.target.value)}
    />
    <button onClick={addItem}>Add</button>
      {/*The unordered list of items to be added to the list. */}
      <ol>
        <li>{}</li>
        <li>Mike</li>
      </ol>
    </div>
  );
}

export default App;
