import React,{useState} from 'react';
import './App.css';
//import statement are placed above this line 


function App() {
  //useState - hook
  const [item, setItem] = useState("");

  //The helper function to help add values to the list.

  return (
    <div className="App">
      {/* The header of this project is below this line. */}
     <h1>TodoList React APP!</h1>
      {/* The Input field and the button */}
    <input type="text"
      placeholder='Please enter...'
      value={item}
      onChange={e => setItem(e.target.value)}
    />
    <button onc>Add</button>
      {/*The unordered list of items to be added to the list. */}
      <ol>
        <li>Mambwe</li>
        <li>Mike</li>
      </ol>
    </div>
  );
}

export default App;
