import './App.css'
function App() {
  //We create an Array of objects for the sound tiles.
  //The audio clips array

  const drumPads = [
    {
      keyCode: 81,
      text: "Q",

      src: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 87,
      text: "W",

      src: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 69,
      text: "E",

      src: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 65,
      text: "A",

      src: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 83,
      text: "S",

      src: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 68,
      text: "D",

      src: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 90,
      text: "Z",

      src: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 88,
      text: "X",

      src: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 67,
      text: "C",

      src: "https://s3.amazonaws.com/freecodecamp...",
    },
  ];
  //Array body ends here.
  //playsound method starts here
  function playSound(selector){
    const audioTag = document.getElementById(selector);
    console.log(audioTag);
    audioTag.currentTime = 0;
    audioTag.play();

  }
  //return starts here
  return (
    <div className="App">
      <div id="drum-machine">
        <div id="display"></div>
        <div className="drum-pads">
        {drumPads.map((drumPad)=>(
        <div 
          onClick={()=>{
          playSound(drumPad.text)
          }} 
          className="drum-pad" 
          id={drumPad.text}
          >
          {drumPad.text}
          <audio className='clip'
           id={drumPad.text} 
          src={drumPad.src}
          ></audio>
        </div>
        ))}</div>
      </div>
    </div>
  );
}

export default App;
