import './App.css'
function App() {
  //We create an Array of objects for the sound tiles.
  //The audio clips array

  const drumPads = [
    {
      keyCode: 81,
      text: "Q",
      id: "Closed-HH",
      url: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 87,
      text: "W",
      id: "Closed-HH",
      url: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 69,
      text: "E",
      id: "Closed-HH",
      url: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 65,
      text: "A",
      id: "Closed-HH",
      url: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 83,
      text: "S",
      id: "Closed-HH",
      url: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 68,
      text: "D",
      id: "Closed-HH",
      url: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 90,
      text: "Z",
      id: "Closed-HH",
      url: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 88,
      text: "X",
      id: "Closed-HH",
      url: "https://s3.amazonaws.com/freecodecamp...",
    },
    {
      keyCode: 67,
      text: "C",
      id: "Closed-HH",
      url: "https://s3.amazonaws.com/freecodecamp...",
    },
  ];
  //Array body ends here.
  //playsound method starts here
  function playSound(selector){
    const audio = document.getElementById(selector);
    audio.play();

  }
  //return starts here
  return (
    <div className="App">
      <div id="drum-machine">
        <div id="display"></div>
        <div className="drum-pads">
        {drumPads.map((dramPad)=>
        <div onClick={()=>{
          playSound(dramPad.text)
          }} className="drum-pad" id={dramPad.src}>{dramPad.text}
          <audio className='clip' id={dramPad.text}  src={drumPad.src}></audio>
        </div>
        )}</div>
      </div>
    </div>
  );
}

export default App;
